import React from "react";
import { useNavigate, Routes, Route } from 'react-router-dom';
import Postings from "../dash-components/Postings";
import Settings from "../dash-components/Settings";

export default function Dashboard() {
  const navigate = useNavigate();

  const logout = () => {
    sessionStorage.removeItem('userID');
    navigate('/login');
  };

  const handlePanels = (dashPath) => {
    navigate(dashPath);
  };

  return (
    <div className="d-flex flex-mobile" style={{ minHeight: '60vh' }}>
      <div className="side-panel">
        <div className="glass-container">
          <PostsButton />
          <SettingsButton />
          <LogoutButton />
        </div>
      </div>
      <div className="main-panel">
        <div id="main-container" className="glass-container">
          <Settings/>
        </div>
      </div>
    </div>    
  );

  function LogoutButton() {
    return (
      <button onClick={logout} className="btn btn-side-panel">Logout</button>
    );
  }

  function PostsButton() {
    return (
      <button onClick={() => handlePanels('/login/' + sessionStorage.getItem('userID') + '/posts')} className="btn btn-side-panel">
        <i class="bi bi-stickies-fill"></i> 
        Posts
      </button>
    );
  }

  function SettingsButton() {
    return (
      <button onClick={() => handlePanels('/login/' + sessionStorage.getItem('userID') + '/settings')} className="btn btn-side-panel">
        <i class="bi bi-gear-fill"></i>
        Settings
      </button>
    );
  }
}
