// Settings.js

import React from 'react';

function Settings(){
  return (
    <div style={{display:'flex', padding: '1em', height: '85%', overflowY: 'scroll', width: '100%',flexDirection:'column' }}>
      <h1 style={{ color: 'white', width: '100%', height:'fit-content'}}>Settings</h1>
      <div className="w-100 p-3">
        <h3 style={{color:'white'}}><i class="bi bi-person-circle"></i> Account Management</h3>
        <label htmlFor="username" className='mt-3'>Username</label>
        <input type="text" name="username" id="username" className='form-control mt-3'/>
        <label htmlFor="password" className='mt-3'>Password</label>
        <input type="text" name="password" id="password" className='form-control mt-3'/>
      </div>
    </div>
  );
};

export default Settings;
