import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Postings() {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    loadPosts();
  }, []);
  

  const loadPosts = () => {
    axios.post('http://localhost/react-apis/unc-communicator/post-reciever/')
      .then(function (response) {
        const postsWithId = response.data.map((post, index) => ({ ...post, id: index }));
        setPosts(postsWithId);
      })
      .catch(function (error) {
        console.error('Error during login API request:', error);
      });
  };
  


  const appendPost = (postText, id) => {
    const newPost = (
      <div className="post" key={id} id={id}>
        <span className='w-100 d-flex justify-content-end'>
          <PostControlButton postID={id} onDelete={deletePost} />
        </span>
        <p>{postText}</p>
      </div>
    );
  
    setPosts([...posts, newPost]);
  };
  
  const deletePost = (postID) => {
    if (postID !== null) {
      setPosts(posts.filter(post => post.id !== postID));
    }
  }

  return (
    <>
    <div style={{ padding: '1em', height: '90%', overflowY: 'scroll', width: '100%' }}>
        <h1 style={{ color: 'white', width: '100%' }}>Postings</h1>
        <div className='post-container'>
          {posts.map(post => (
            <div className="post" key={post.id} id={post.id}>
              <span className='w-100 d-flex justify-content-end'>
                <PostControlButton postID={post.id} onDelete={deletePost} />
              </span>
              <p>{post.postText}</p>
            </div>
          ))}
        </div>
    </div>
    <span className='d-flex w-100 justify-content-end position-sticky' style={{ padding: '0px 20px' }}>
      <AddPostButton />
    </span>
    </>
  );

  function AddPostButton() {
    return (
      <button className='btn btn-add-post shadow rounded-5' style={{ aspectRatio: '1' }}>
        <i className="bi bi-plus-lg"></i>
      </button>
    );
  }

  function PostControlButton({ postID, onDelete }) {
    return (
      <div className="dropdown">
        <button className="btn btn-post-control" type="button" data-bs-toggle="dropdown" aria-expanded="false">
          <i className="bi bi-three-dots"></i>
        </button>
        <ul className="dropdown-menu">
          <li><a className="dropdown-item" href="#">Edit</a></li>
          <li><a className="dropdown-item" href="#" onClick={() => onDelete(postID)}>Delete</a></li>
          <li><a className="dropdown-item" href="#">Info</a></li>
        </ul>
      </div>
    );
  }
}

export default Postings;
