export default function LoginPage(){
    const login = () => {
        //alert("Loggin in ...");
        sessionStorage.setItem('userID', '3');
        window.location.reload();
    }
    const logout = () => {
        //alert("Loggin out ... ");
        sessionStorage.removeItem('userID');
        window.location.reload();
    }
    return(
        <>
        <h1>LoginPage</h1>
        <div style={{height:'100%', display:'flex', justifyContent:'center', alignItems:'center', backgroundColor:'crimson'}}>
            <div className="container d-flex gap-5">
                <input type="text" className="form-control" name="username" id="username"/>
                <input type="text" className="form-control" name="password" id="password"/>
            </div>
            <div className="container mt-3 d-flex gap-5">
                <LoginButton/>
                <LogoutButton/>
            </div>
        </div>
        </>
    );
    function LoginButton(){
        return(
            <button onClick={login} className="btn btn-success">Login</button>
        );
    }
    function LogoutButton(){
        return(
            <button onClick={logout} className="btn btn-danger">Logout</button>
        );
    }

}
