//import { Link } from "react-router-dom";
import React,{ useEffect, useState } from "react";
import axios from "axios";

export default function LoginPage(){
    const [userID, setUserID] = useState(0);
    const [inputs, setInputs] = useState({
        username: '',
        password: '',
    });
    
    const handleChange = (e) => {
        const { name, value } = e.target;
        setInputs((prevInputs) => ({
          ...prevInputs,
          [name]: value,
        }));
    };
    
    const login = () => {
        if (Object.values(inputs).every((input) => input !== '')) {
          axios.post('http://localhost/react-apis/unc-communicator/login-api/', inputs)
          .then(function (response) {
            // Check if response.data[0] is defined and has 'userID'
            if (response.data[0] && response.data[0]['userID'] !== undefined) {
                setUserID(response.data[0]['userID']);
            } else {
                console.error('Invalid or missing user data in the response:', response.data);
            }
        })
        .catch(function (error) {
            console.error('Error during login API request:', error);
        });
        }
    };

    useEffect(() => {
        // This will run after the state has been updated
        console.log(userID);
    }, [userID]); // Add userID as a dependency

    return(
        <>
        <div style={{height:'90vh', display:'flex', flexDirection:'column', justifyContent:'center', alignItems:'center'}}>
            <div className="container d-flex flex-column gap-5 w-50 rounded-4" style={{border:'3px solid var(--com-pink)', padding:'30px'}}>
                <label htmlFor="username">Username</label>
                <input type="text" className="form-control" name="username" id="username" value={inputs.username} onChange={handleChange}/>
                <label htmlFor="password">Password</label>
                <input type="text" className="form-control" name="password" id="password" value={inputs.password} onChange={handleChange}/>
                <div className="d-flex justify-content-between w-100 gap-3">
                    <LoginButton/>
                </div>
            </div>
        </div>
        </>
    );
    function LoginButton(){
        return(
            <button onClick={login} className="btn btn-success w-100">
                Login
            </button>
        );
    }
}
