import React from "react";
import { useNavigate, Routes, Route } from 'react-router-dom';
import Posts from "../dash-components/Posts";

export default function Dashboard() {
  const navigate = useNavigate();

  const logout = () => {
    sessionStorage.removeItem('userID');
    navigate('/login');
  };

  const handlePanels = (dashPath) => {
    navigate(dashPath);
  };

  return (
    <div className="d-flex flex-mobile" style={{ minHeight: '60vh' }}>
      <div className="side-panel">
        <div className="glass-container">
          <PostsButton />
          <SettingsButton />
          <LogoutButton />
        </div>
      </div>
      <div className="main-panel">
        <div className="glass-container">
          
        </div>
      </div>
    </div>
  );

  function LogoutButton() {
    return (
      <button onClick={logout} className="btn btn-side-panel">
        Logout
      </button>
    );
  }

  function PostsButton() {
    return (
      <button
        onClick={() => handlePanels('/posts')}
        className="btn btn-side-panel"
      >
        Posts
      </button>
    );
  }

  function SettingsButton() {
    return (
      <button
        onClick={() => handlePanels('/settings')} // Example path, replace with the actual path
        className="btn btn-side-panel"
      >
        Settings
      </button>
    );
  }
}
