import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Postings() {
  /*Post Functions*/
  const [posts, setPosts] = useState([]);

  useEffect(() => { // load posts on form load
    loadPosts();
  }, []);

  const loadPosts = () => {
    axios.post('http://localhost/react-apis/unc-communicator/post-manager/')
      .then(function (response) {
        const postsWithId = response.data.map((post, index) => ({ ...post, id: index }));
        setPosts(postsWithId);
      })
      .catch(function (error) {
        console.error('Error during post API request:', error);
      });
  };

  const sendPost = (messageText,messageCaption) => {
    const input = [messageText,messageCaption];
    axios.post('http://localhost/react-apis/unc-communicator/post-manager/',input)
    .then(function (response) {
      console.log(response.data);
    })
    .catch(function (error) {
      console.error('Error during post API request:', error);
    });
  }
  
  const deletePost = (postID) => { // delete post fun
    const shouldDelete = window.confirm('Are you sure you want to delete this post?');
    if (shouldDelete && postID !== null) {
      setPosts(posts.filter(post => post.id !== postID));
    }
  }

  /*Modal Functions*/    
  const [isModalOpen, setModalOpen] = useState(false);

  const openModal = () => {
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
  };


  return (
    <>
    <div style={{ padding: '1em', height: '85%', overflowY: 'scroll', width: '100%' }}>
        <h1 style={{ color: 'white', width: '100%' }}>Your Posts</h1>
        <div className='post-container'>
          {posts.map(post => (
            <div className="post" key={post.id} id={post.id}>
              <span className='w-100 d-flex justify-content-between'>
                <span className="badge text-bg-primary" style={{height:'fit-content'}}>{post.postType}</span>
                <small className='badge text-info text-wrap text-end'>{post.postCaption}</small>
              </span>              
              <span className='w-100 d-flex justify-content-end'>
                <PostControlButton postID={post.id} onDelete={deletePost} />
              </span>
              
              <p>{post.postText}</p>
            </div>
          ))}
        </div>
    </div>
    <span className='d-flex w-100 justify-content-between gap-1 align-items-center position-fixed rounded-4' style={{padding:'0px 20px',  bottom:'0', height:'15%' }}>      
      <input type='text' name="message-box" id="message-box" className="form-control form-control-sm rounded-3"/>      
      <AddPostButton />
    </span>
    {isModalOpen ? ( // Post Modal
      <div className="post-modal rounded-4 shadow">
        <div className="post-modal-content">
          <span className='w-100 d-flex justify-content-between align-items-center'>
            <p style={{padding:'0px',margin:'0px'}}>Select Post Type</p>
            <button onClick={closeModal} className='btn glass-container' style={{width:'fit-content',color:'white'}}>
              <i className="bi bi-x"></i>
            </button>
          </span>
          <ul className='post-modal-list'>
            <li onClick={closeModal}><i className="bi bi-chat-left-text-fill"></i> Text Message</li>
            <li onClick={closeModal}><i className="bi bi-link"></i> URL Message</li>
            <li onClick={closeModal}><i className="bi bi-image-fill"></i> Image</li>
          </ul>
        </div>
      </div>
    ) : (<div></div>)}
    </>
  );

  function AddPostButton() {
    return (
      <button className='btn btn-add-post shadow rounded-5' style={{ aspectRatio: '1' }} onClick={openModal}>
        <i className="bi bi-plus-lg"></i>
      </button>
    );
  }

  function PostControlButton({ postID, onDelete }) {
    return (
      <div className="dropdown">
        <button className="btn btn-post-control" type="button" data-bs-toggle="dropdown" aria-expanded="false">
          <i className="bi bi-three-dots"></i>
        </button>
        <ul className="dropdown-menu">
          <li><a className="dropdown-item" href="#">Edit</a></li>
          <li><a className="dropdown-item" href="#" onClick={() => onDelete(postID)}>Delete</a></li>
          <li><a className="dropdown-item" href="#">Info</a></li>
        </ul>
      </div>
    );
  }
}

export default Postings;
