import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';

function Postings() {
  /*useState vars*/

  const [allPosts, setAllPosts] = useState([]); // unfiltered posts
  const [posts, setPosts] = useState([]);
  const [spinnerVisible, setSpinner] = useState(false); // spinner state
  const [imageInput, setImageInput] = useState({});

  /*useEffect hooks*/

  useEffect(() => { // load posts on form load
    loadPosts();
  }, []);

  useEffect(() => { // prints / uploads image data on updation of 'imageInput'
    if(Object.keys(imageInput).length > 0){
      console.log(imageInput);
      
      // Upload image data using axios
      axios.post('http://localhost/react-apis/unc-communicator/img-manager/',imageInput)
      .then(function (response) {
        console.log(response.data);
        //window.location.reload();
      })
      .catch(function (error) {
        console.error('Error during post API request:', error);
      });
    }
  }, [imageInput]);

  /*Post Functions*/

  const loadPosts = () => { // load post fun
    axios.get('http://localhost/react-apis/unc-communicator/post-manager/')
      .then(function (response) {
        const postsWithId = response.data.map((post, index) => ({ ...post, id: index }));
        setAllPosts(postsWithId);
        setPosts(postsWithId);
      })
      .catch(function (error) {
        console.error('Error during post API request:', error);
      });
  };

  const sendPost = (messageType,messageText,currentTime) => { // send post fun
    switch (messageType) {
      case "message": // Normal Message
        if(messageText.length < 11000 && messageText.length > 0){
          const messageCaption = messageText.split('|')[0];
          const messageText2 = messageText.split('|')[1];
          const input = [messageType,messageText2,messageCaption,currentTime,currentTime];
          axios.post('http://localhost/react-apis/unc-communicator/post-manager/',input)
          .then(function (response) {
            console.log(response.data);
            window.location.reload();
          })
          .catch(function (error) {
            console.error('Error during post API request:', error);
          });  
        }else{
          alert("Invalid Input!");
          window.location.reload();
        }    
        break;
    
      case "URL": // URL Message
        if(messageText.length < 11000 && messageText.length > 0){
          // Get Site Title API
          axios.post('http://localhost/react-apis/url-title-api/?url=' + messageText)
          .then(function (response) {
            console.log(response.data["title"]);
            // INSERT Rec API
            const input = [messageType,messageText,response.data["title"],currentTime,currentTime];
            axios.post('http://localhost/react-apis/unc-communicator/post-manager/',input)
            .then(function (response) {
              console.log(response.data);
              window.location.reload();
            })
            .catch(function (error) {
              console.error('Error during post API request:', error);
            });
          })
          .catch(function (error) {
            console.error('Error during post API request:', error);
          });  
        }else{
          alert("Invalid Input!");
          window.location.reload();
        }  
        break;
    }
  }

  const deletePost = (postID) => { // delete post fun
    const shouldDelete = window.confirm('Are you sure you want to delete this post?');
    if (shouldDelete && postID !== null) {
      setPosts(posts.filter(post => post.id !== postID));
    }
  }

  const filterMessages = (postType) => { // filter posts fun
    switch (postType) {
      case "All":
        setPosts(allPosts); // Reset to all posts
        break;
    
      case "Text":
        setPosts(allPosts.filter(post => post.postType === "message"));
        break;

      case "URL":
        setPosts(allPosts.filter(post => post.postType === "URL"));
        break;

      case "Image":
        //setPosts(allPosts.filter(post => post.postType === "Image"));
        break;
    }
  }

  /*Image Uploader*/

  const fileInputRef = useRef(null);

  const imgUploader = (imgFile, imgLocation, imgName, imgCaption, currentTime) => { // image uploader fun

    if (imgCaption.length > 0) {
      /*
      setImageInput(prevState => ({
        ...prevState,
        Image_File: imgFile,
        Image_Location: imgLocation,
        Image_Caption: imgCaption,
        Image_Created: currentTime,
        Image_Modified: currentTime
      }));
      */
    } else {
      setImageInput(prevState => ({
        ...prevState,
        Image_File: imgFile,
        Image_Location: imgLocation,
        Image_Caption: imgName,
        Image_Created: currentTime,
        Image_Modified: currentTime
      }));
    }
  };

  function handleImageSelecter(){
    // Trigger the click event of the hidden file input
    fileInputRef.current.click();
  };

  const handleFileChange = (event) => {
    const selectedFile = event.target.files[0];

    const today = new Date();
    const date = today.getFullYear() + '-' + (today.getMonth() + 1) + '-' + today.getDate();
    const time = today.getHours() + ':' + today.getMinutes() + ':' + today.getSeconds();
    const dateTime = date + ' ' + time;

    imgUploader(selectedFile, document.getElementById('imgSel').value, selectedFile["name"], document.getElementById('message-box').value, dateTime);
    setSpinner(true);
};
  
  /*Modal Functions*/ 

  const [isModalOpen, setModalOpen] = useState(false);

  const openModal = () => {
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
  };


  return (
    <>
    <div style={{ padding: '1em', height: '85%', overflowY: 'scroll', width: '100%' }}>
        <h1 style={{ display:'flex', justifyContent:'space-between',alignItems:'center', color: 'white', width: '100%' }}>
          Your Posts 
          <FilterButton/>
        </h1>
        <div className='post-container'>
          {posts.map(post => (
            <div className="post" key={post.id} id={post.id}>
              <span className='w-100 d-flex justify-content-between'>
                <span className="badge text-bg-primary" style={{height:'fit-content'}}>{post.postType}</span>
                <small className='badge text-info text-wrap text-end'>{post.postCaption}</small>
              </span>              
              <span className='w-100 d-flex justify-content-end'>
                <PostControlButton postID={post.id} onDelete={deletePost} />
              </span>
              <p style={{overflow:'hidden',textOverflow:'ellipsis'}}>{ post.postType == 'URL' ? ( <a href={post.postText} className='post-text-url' role='p'>{post.postText}</a> ) : post.postText}</p>
            </div>
          ))}
        </div>
    </div>
    <span className='d-flex w-100 justify-content-between gap-1 align-items-center position-fixed rounded-4' style={{padding:'0px 20px',  bottom:'0', height:'15%' }}>      
      <textarea name="message-box" id="message-box" className="form-control form-control-sm rounded-3" placeholder='Caption | Message Content'/>
      <AddPostButton />
    </span>
    {isModalOpen ? ( // Post Modal
      <div className="post-modal rounded-4 shadow">
        <div className="post-modal-content">
          <span className='w-100 d-flex justify-content-between align-items-center'>
            <p style={{padding:'0px',margin:'0px'}}>Select Post Type</p>
            <span className={spinnerVisible ? "spinner-border " : ""} aria-hidden="true"></span>
            <button onClick={closeModal} className='btn glass-container' style={{width:'fit-content',color:'white'}}>
              <i className="bi bi-x"></i>
            </button>
          </span>
          <ul className='post-modal-list'>
            <li onClick={() => {
                                var today = new Date();
                                var date = today.getFullYear()+'-'+(today.getMonth()+1)+'-'+today.getDate();
                                var time = today.getHours() + ":" + today.getMinutes() + ":" + today.getSeconds();
                                var dateTime = date+' '+time;
                                sendPost('message', document.getElementById('message-box').value, dateTime);
                                setSpinner(true);
                        }}><i className="bi bi-chat-left-text-fill"></i> Text Message</li>
            <li onClick={() => {
                                var today = new Date();
                                var date = today.getFullYear()+'-'+(today.getMonth()+1)+'-'+today.getDate();
                                var time = today.getHours() + ":" + today.getMinutes() + ":" + today.getSeconds();
                                var dateTime = date+' '+time;
                                sendPost('URL', document.getElementById('message-box').value, dateTime);
                                setSpinner(true);
                        }}><i className="bi bi-link"></i> URL Message</li>
            <li onClick={() => { handleImageSelecter(); }}>
              <i className="bi bi-image-fill"></i>
              <input id="imgSel" type="file" ref={fileInputRef} style={{ display: 'none' }} onChange={handleFileChange} />
              Select an Image for Upload
            </li>
          </ul>
        </div>
      </div>
    ) : (<div></div>)}
    </>
  );

  function AddPostButton() {
    return (
      <button className='btn btn-add-post shadow rounded-5' style={{ aspectRatio: '1' }} onClick={openModal}>
        <i className="bi bi-plus-lg"></i>
      </button>
    );
  }

  function PostControlButton({ postID, onDelete }) {
    return (
      <div className="dropdown">
        <button className="btn btn-post-control" type="button" data-bs-toggle="dropdown" aria-expanded="false">
          <i className="bi bi-three-dots"></i>
        </button>
        <ul className="dropdown-menu">
          <li><a className="dropdown-item" href="#">Edit</a></li>
          <li><a className="dropdown-item" href="#" onClick={() => onDelete(postID)}>Delete</a></li>
          <li><a className="dropdown-item" href="#">Info</a></li>
        </ul>
      </div>
    );
  }

  function FilterButton(){
    return(
      <div className="dropdown p-0 m-0" style={{height:'100%',display:'flex',alignItems:'center'}}>
        <button className="btn glass-container" style={{width:'fit-content',color:'white',height:'100%'}}  data-bs-toggle="dropdown" aria-expanded="false">
          <i className="bi bi-filter"></i>
        </button>
        <ul className="dropdown-menu">
          <li><a className="dropdown-item" href="#" onClick={() => filterMessages('All')}>All Messages</a></li>
          <li><a className="dropdown-item" href="#" onClick={() => filterMessages('Text')}>Text Messages</a></li>
          <li><a className="dropdown-item" href="#" onClick={() => filterMessages('URL')}>URL Messages</a></li>
          <li><a className="dropdown-item" href="#" onClick={() => filterMessages('Images')}>Images</a></li>
        </ul>
      </div>
    );
  }
}

export default Postings;
