import { Link } from "react-router-dom";
export default function LoginPage(){
    const login = () => {
        //alert("Loggin in ...");
        sessionStorage.setItem('userID', '3');
        window.location.reload();
    }
    const logout = () => {
        //alert("Loggin out ... ");
        sessionStorage.removeItem('userID');
        window.location.reload();
    }
    return(
        <>
        <div style={{height:'500px', display:'flex', flexDirection:'column', justifyContent:'center', alignItems:'center'}}>
            <div className="container d-flex flex-column gap-5 w-50" style={{border:'3px solid var(--com-pink)', padding:'30px'}}>
                <label htmlFor="username">Username</label>
                <input type="text" className="form-control" name="username" id="username"/>
                <label htmlFor="password">Password</label>
                <input type="text" className="form-control" name="password" id="password"/>
            </div>
            <div className="container mt-3 d-flex gap-5 w-50">
                <LoginButton/>
                <LogoutButton/>
                <Link to={`/login/${sessionStorage.getItem('userID')}`}>Login</Link>
            </div>
        </div>
        </>
    );
    function LoginButton(){
        return(
            <button onClick={login} className="btn btn-success">
                Login
            </button>
        );
    }
    function LogoutButton(){
        return(
            <button onClick={logout} className="btn btn-danger">Logout</button>
        );
    }

}
