// Settings.js
import React, { useState } from 'react';

const [username, setUsername] = useState("");
const [password, setPassword] = useState("");

/*handle text change*/
const handleUsernameChange = (e) => {
  setUsername(e.target.value);
  console.log(username);
}
const handlePasswordChange = (e) => {
  setPassword(e.target.value);
  console.log(password);
}

const updateProfile = () => {
  axios.post('http://localhost/react-apis/unc-communicator/post-manager/',input)
  .then(function (response) {
    console.log(response.data);
  })
  .catch(function (error) {
    console.error('Error during post API request:', error);
  });  
}

function Settings(){
  return (
    <div style={{display:'flex', padding: '1em', height: '85%', overflowY: 'scroll', width: '100%',flexDirection:'column' }}>
      <h1 style={{ color: 'white', width: '100%', height:'fit-content'}}>Settings</h1>
      <hr />
      <div className="w-100 p-3">
        <h3 style={{color:'white'}}><i className="bi bi-person-circle"></i> Account Management</h3>
        <label htmlFor="username" className='mt-3'>Username</label>
        <input type="text" name="username" id="username" className='form-control mt-3' onChange={handleUsernameChange} value={username}/>
        <label htmlFor="password" className='mt-3'>Password</label>
        <input type="text" name="password" id="password" className='form-control mt-3' onChange={handleUsernameChange} value={username}/>
        <SaveButton/>
        <hr />
      </div>
    </div>
  );
};

function SaveButton(){
  return(
    <button className='btn btn-primary mt-3' onClick={()=> updateProfile()}>Save</button>
  );
}

export default Settings;
