//import { Link } from "react-router-dom";
import { useState } from "react";
import axios from "axios";

export default function LoginPage(){
    const login = () => {
        let inputs = [Uname, Uemail, Utel];
        if (inputs.every((input) => input !== '')) {
            axios.post('http://localhost/react-apis/unc-communicator/login-api/',inputs).then(function(response){
                console.log(response.data);
            });    
        }
        //sessionStorage.setItem('userID', '3');
        //window.location.href = `/login/${sessionStorage.getItem('userID')}`;
        //window.location.reload();
    }
    return(
        <>
        <div style={{height:'90vh', display:'flex', flexDirection:'column', justifyContent:'center', alignItems:'center'}}>
            <div className="container d-flex flex-column gap-5 w-50 rounded-4   " style={{border:'3px solid var(--com-pink)', padding:'30px'}}>
                <label htmlFor="username">Username</label>
                <input type="text" className="form-control" name="username" id="username"/>
                <label htmlFor="password">Password</label>
                <input type="text" className="form-control" name="password" id="password"/>
                <div className="d-flex justify-content-between w-100 gap-3">
                    <LoginButton/>
                </div>
            </div>
        </div>
        </>
    );
    function LoginButton(){
        return(
            <button onClick={login} className="btn btn-success w-100">
                Login
            </button>
        );
    }
}
