import React from "react";
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Posts from "../dash-components/Posts";
export default function Dashboard(){
    const logout = () => {
        //alert("Loggin out ... ");
        sessionStorage.removeItem('userID');
        window.location.href = '/login';
    }
    const handlePanels = ({dashPath}) =>{
        window.location.href = window.location.href + dashPath;
    }
    return(
        <div className="d-flex flex-mobile" style={{minHeight:'60vh'}}>
            <div className="side-panel">
                <div className="glass-container">                    
                    <PostsButton/>
                    <SettingsButton/>
                    <LogoutButton/>
                </div>
            </div>
            <div className="main-panel">
                <div className="glass-container">                    
                        <Routes>
                            <Route path="/posts" element={<Posts/>}/>
                        </Routes>                    
                </div>
            </div>
        </div>
    );
    
    function LogoutButton(){
        return(
            <button onClick={logout} className="btn btn-side-panel">Logout</button>
        );
    }
    function PostsButton() {
        return (
          <button onClick={handlePanels}  dashPath={'/posts'} className="btn btn-side-panel">Posts</button>
        );
    }
    function SettingsButton() {
        return (
          <button onClick={handlePanels} dashPath={'/posts'} className="btn btn-side-panel">Settings</button>
        );
    }
}
